------------------------------------------------------------------------------------------------------------------------
-- Create default joke data
------------------------------------------------------------------------------------------------------------------------

Declare @RemovePreviousJokes varchar(1) = 'N' --   --> don't delete the existing data!

Declare @tmpCategory Table (
  JokeCategoryTxt nvarchar(500)
)
Declare @tmpJokes Table (
  JokeId int identity(1,1),
  JokeTxt nvarchar(max),
  JokeCategoryTxt nvarchar(500),
  Attribution nvarchar(500),
  ImageTxt nvarchar(max)
)

------------------------------------------------------------------------------------------------------------------------
Delete From @tmpCategory

  ('Science'),

  ('What do you call...')

Delete From @tmpJokes

------------------------------------------------------------------------------------------------------------------------
IF @RemovePreviousJokes = 'Y'
BEGIN
    Print 'Removing previous set of jokes...'
    Delete From JokeRating
    Delete From JokeCategory
    Delete From Joke
    DBCC CHECKIDENT('JokeRating', RESEED, 1)
    DBCC CHECKIDENT('JokeCategory', RESEED, 1)
    DBCC CHECKIDENT('Joke', RESEED, 1)
END

Print 'Inserting fresh categories...'
Select JokeCategoryTxt From @tmpCategory Where JokeCategoryTxt NOT IN (Select JokeCategoryTxt From JokeCategory)

Print 'Inserting fresh jokes...'
    Select c.JokeCategoryId, c.JokeCategoryTxt, j.JokeTxt, j.Attribution, j.ImageTxt, 0, 0
    From @tmpJokes j INNER JOIN JokeCategory c on j.JokeCategoryTxt = c.JokeCategoryTxt
    Where j.JokeTxt NOT IN (Select JokeTxt From Joke)

Print 'Displaying All Jokes...'
Select j.JokeId, j.JokeCategoryId, j.JokeCategoryTxt, j.JokeTxt, j.ImageTxt, j.Rating, j.CreateDateTime From Joke j ORDER BY j.JokeCategoryTxt, j.JokeTxt
